package com.company.auth.entities;import com.company.base.BaseEntity;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.Table;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;@Setter@Getter@NoArgsConstructor@Entity@Table(name = "email_history")public class EmailHistoryEntity extends BaseEntity {    @Column(name = "message", columnDefinition = "TEXT")    private String message;    @Column(name = "email")    private String email;}