package com.company.auth.repositories;import com.company.auth.entities.EmailHistoryEntity;import org.springframework.data.jpa.repository.JpaRepository;import java.time.LocalDateTime;import java.util.UUID;public interface EmailHistoryRepository extends JpaRepository<EmailHistoryEntity, UUID> {    boolean existsByEmail(String email);    void deleteByEmail(String email);    Long countAllByEmailAndCreatedDateAfter(String email, LocalDateTime localDateTime);}